    /**
     * 
     *
     * @param contractBytecode
     * @param contractName     
     * @return
     */
    public ContractOuterClass.Contract setupContract(String contractBytecode, String contractName) {
        // 1. Create contract construction parameter (RLP encoding)
        Function function = new Function("", Collections.singletonList(new Address(clientAddress)),
                Collections.emptyList());
        String methodDataStr = FunctionEncoder.encode(function);
        Map<String, byte[]> paramMap = new HashMap<>();
        paramMap.put(CONTRACT_ARGS_EVM_PARAM, methodDataStr.substring(10).getBytes());

        // 2. create payload
        Request.Payload payload = null;
        try {
            payload = chainClient.createContractCreatePayload(
                    Utils.calcContractName(contractName),
                    "1",
                    contractBytecode.getBytes(),
                    ContractOuterClass.RuntimeType.EVM,
                    paramMap);
        } catch (ChainMakerCryptoSuiteException e) {
            throw new RuntimeException("fail to create" + contractName + "payload", e);
        }

        // 3. create payload with endorsement
        User[] adminUserArray = adminUserList.toArray(new User[adminUserList.size()]);
        Request.EndorsementEntry[] endorsementEntries = null;
        try {
            endorsementEntries = SdkUtils.getEndorsers(payload, adminUserArray);
        } catch (ChainMakerCryptoSuiteException | UtilsException e) {
            throw new RuntimeException("fail to create" + contractName + "payload with endorsement", e);
        }

        // 4. send request
        ResultOuterClass.TxResponse responseInfo = null;
        try {
            responseInfo = chainClient.sendContractManageRequest(
                    payload, endorsementEntries, rpcCallTimeout, syncResultTimeout);
        } catch (ChainMakerCryptoSuiteException | ChainClientException e) {
            throw new RuntimeException("failed to send request for " + contractName, e);
        }

        if (responseInfo.getCode() != ResultOuterClass.TxStatusCode.SUCCESS) {
            throw new RuntimeException("failed to deploy contract" + contractName);
        }

        // 5. get Contract
        ContractOuterClass.Contract contract;
        try {
            contract = ContractOuterClass.Contract.newBuilder()
                    .mergeFrom(responseInfo.getContractResult().getResult().toByteArray()).build();
        } catch (InvalidProtocolBufferException e) {
            throw new RuntimeException("failed to get contract from deploy contract response for " + contractName, e);
        }

        return contract;
    }

    /**
     * 
     *
     * @param contractName 
     * @param methodName
     * @param inputParams
     * @param outputParams
     * @return
     */
    public ResultOuterClass.TxResponse invokeContract(String contractName,
                                                      String methodName,
                                                      List<Type> inputParams,
                                                      List<TypeReference<?>> outputParams) {

        Map<String, byte[]> params = new HashMap<>();
        Function function = new Function(methodName, inputParams, outputParams);
        String methodDataStr = FunctionEncoder.encode(function);
        String method = methodDataStr.substring(0, 10);
        params.put(CONTRACT_ARGS_EVM_PARAM, methodDataStr.getBytes());

        ResultOuterClass.TxResponse responseInfo = null;
        try {
            return chainClient.invokeContract(contractName,
                    method, null, params, rpcCallTimeout, syncResultTimeout);

        } catch (ChainMakerCryptoSuiteException | ChainClientException | RuntimeException e) {
            throw new RuntimeException(
                    StrUtil.format(
                            "failed to invoke contract {} with method {}",
                            contractName,
                            methodName
                    ), e
            );
        }
    }

    /**
     * 
     *
     * @param contractName
     * @param methodName
     * @param inputParams
     * @param outputParams
     * @return
     */
    public ResultOuterClass.TxResponse queryContract(String contractName,
                                                     String methodName,
                                                     List<Type> inputParams,
                                                     List<TypeReference<?>> outputParams) {

        Map<String, byte[]> params = new HashMap<>();
        Function function = new Function(methodName, inputParams, outputParams);
        String methodDataStr = FunctionEncoder.encode(function);
        String method = methodDataStr.substring(0, 10);
        params.put(CONTRACT_ARGS_EVM_PARAM, methodDataStr.getBytes());

        try {
            return chainClient.queryContract(contractName,
                    method, null, params, rpcCallTimeout);
        } catch (ChainMakerCryptoSuiteException | ChainClientException | RuntimeException e) {
            throw new RuntimeException(
                    StrUtil.format(
                            "failed to query contract {} with method {}",
                            contractName,
                            methodName
                    ), e
            );
        }
    }
